import React, {useEffect, useState} from 'react';
import { Modal, Form, Button } from 'react-bootstrap';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import swal from 'sweetalert';

function Company() {
  //
  const [loading, setLoading] = useState(true);
  const [company, setCompany] = useState([]);
  const [companyInput, setCompanyInput] = useState({
      name: '',
      tag: '',
      description: '',
      error_list: [],
  });
  const [show, setShow] = useState(false);

  //
  useEffect(() => {
      let isMountered = true;

      axios.get('api/list-company').then(res => {
          if(isMountered)
          {
              if(res.data.status === 200)
              {
                  //
                  console.log(res.data.companies);
                  setCompany(res.data.companies);
                  setLoading(false);
              }
          }
      });

      return () => {
          isMountered = false;
      }
  });

  //
  const handleShow = () => setShow(true);
  const handleClose = () => setShow(false);

  //
  const history = useNavigate();

  //
  const handleInput = (e) => {
      e.persist();
      setCompanyInput({...categoryInput, [e.target.name]: e.target.value});
  }

  //
  if(loading)
  {
      return <h4>Loading Companies...</h4>
  }
  else
  {
      var showCompanies = '';
      showCompanies = company.map((item, idx) => {
          return (
            <tr key={idx}>
              <th scope="col">{++idx}</th>
              <td>{item.name}</td>
              <td>{item.tag}</td>
              <td>{item.description}</td>
            </tr>
          )
      });
  }

  return (
      <div>
        <section className="masthead bg-default">
              <div className="container">
                <div className="row mb-3">
                  <div className="col-lg-12">
                    <button className="btn btn-secondary btn-sm float-end">Open new company</button>
                  </div>
                </div>

                <div className="row">
                  <table className="table table-hover">
                    <thead>
                      <tr>
                        <th scope="col">#</th>
                        <th scope="col">Company</th>
                        <th scope="col">Tag</th>
                        <th scope="col">Description</th>
                      </tr>
                    </thead>
                    <tbody>
                      {showCompanies}
                    </tbody>
                  </table>
                </div>
              </div>
          </section>
    </div>
  )
}

export default Company;
