import React, {useEffect, useState} from 'react';
import { Modal, Button } from 'react-bootstrap';
import axios from 'axios';
import {Link} from 'react-router-dom';
import AddForm from './AddForm';

function Category() {
  //
  const [loading, setLoading] = useState(true);
  const [category, setCategory] = useState([]);

  //
  useEffect(() => {
      let isMountered = true;

      axios.get('api/list-category').then(res => {
          if(isMountered)
          {
              if(res.data.status === 200)
              {
                  //
                  console.log(res.data.categories);
                  setCategory(res.data.categories);
                  setLoading(false);
              }
          }
      });

      return () => {
          isMountered = false;
      }
  });

  //
  const [show, setShow] = useState(false);

  //
  const handleShow = () => setShow(true);
  const handleClose = () => setShow(false);

  //
  if(loading)
  {
      return <h4>Loading Categories...</h4>
  }
  else
  {
      var showCategories = '';
      showCategories = category.map((item, idx) => {
          return (
            <tr key={idx}>
              <th scope="col">{++idx}</th>
              <td>{item.name}</td>
              <td>{item.tag}</td>
              <td>{item.description}</td>
            </tr>
          )
      });
  }

  return (
      <div>
        <section className="masthead bg-default">
              <div className="container">
                <div className="row mb-3">
                  <div className="col-lg-12">
                    <button onClick={handleShow} className="btn btn-secondary btn-sm float-end" data-toggle="modal">Open new category</button>
                  </div>
                </div>

                <div className="row">
                  <table className="table table-hover">
                    <thead>
                      <tr>
                        <th scope="col">#</th>
                        <th scope="col">Category</th>
                        <th scope="col">Tag</th>
                        <th scope="col">Description</th>
                      </tr>
                    </thead>
                    <tbody>
                      {showCategories}
                    </tbody>
                  </table>
                </div>
              </div>
          </section>

          <Modal show={show}>
            <Modal.Header>
              <Modal.Title>
                New Category
              </Modal.Title>
            </Modal.Header>
            <Modal.Body>
              <AddForm />
            </Modal.Body>
            <Modal.Footer>
              <Button onClick={handleClose} variant="secondary">Close</Button>
            </Modal.Footer>
          </Modal>
    </div>
  )
}

export default Category;
